  el.id = 'iframe';
  el.marginwidth = '0';
  el.marginheight = '0';
  el.hspace = '0';
  el.vspace = '0';
  el.frameborder = '0';
  el.scrolling = 'no';
  el.width = '100%';
  el.height = '740';
  el.title = 'Chat GPT Next онлайн бесплатно на русском, включает в себя модели GPT-4 / GPT-4 vision / GPT-3.5';

  var target = ''; 

  var frameContainer = document.getElementById('frame-container');
  frameContainer.style.display = 'block';
  frameContainer.appendChild(el);

  var iframe = document.getElementById('iframe');
  iframe.contentWindow.document.open();
  iframe.contentWindow.document.write(target);
  iframe.contentWindow.document.close();
}

